mapper.insert:
INSERT INTO mh_user(
						USER_ID
						, USER_PWD
						, USER_TYPE
						, USER_NAME
						, USER_PHONE
						, USER_EMAIL
						, USER_STATUS
						, USER_ZIP
						, USER_ADDR1
						, USER_ADDR2
						, USER_BIRTHDAY
						, USER_SEX
						, RECEIVE_EMAIL
						, RECEIVE_SMS
						, CREATED_TYPE
						, REQUIRED_AGREE
						, SELECT_AGREE
						, CREATED__DATE
						, UPDATE_DATE
					) VALUES (
							 #{userId}
							, #{userPwd}
							, #{userType}
							, #{userName}
							, #{userPhone}
							, #{userEmail}
							, 'A'
							, #{userZip}
							, #{userAddr1}
							, #{userAddr2}
							, #{userBirthday}
							, #{userSex}
							, 'Y'
							, 'Y'
							, 'M'
							, 'Y'
							, 'Y'
							, NOW()
							, NOW()
						)

mapper.update:
UPDATE mh_user
USER_NAME = #{userName}, USER_PHONE = #{userPhone}, USER_EMAIL = #{userEmail}, USER_ZIP = #{userZip}, USER_ADDR1 = #{userAddr1}, USER_ADDR2 = #{userAddr2},
WHERE USER_ID = #{userId}
